// used by multiple contracts
interface metaVariables {

    // used directly in BookingCarpooling and "CreatingCarpooling"
    enum State{booked, validated, paid, conflicted}


}


contract CreatingCarpooling is metaVariables {
    // carpooling description
    address payable public owner;
    address payable public conflictOwner;

    string public origin;
    string public destination;
    uint8 public nSlot;
    uint8 public nAvailableSlot;
    uint8 public price; // in euro
    uint256 public startTime;

    State public carpoolingState;
    uint8 public nConflict;
    uint8 public nValid;

    enum ModeConflict{generous, normal, tyranic}
    ModeConflict public carpoolingModeConflict;

    // can be used by third party (as Carpooling website), 
    // ex: A allows Carpooling.com to withdraw the price, then A retrieve it form Carpooling.com.
    // in the mean time, A used insurrance.com in case of accident, after everyone has validating the carpooling, insurrance.com can withdraw 1€ from A
    mapping(address => mapping (address => uint256)) public allowances;

    // todo : delete ??
    uint256 public ownerSupply;// TODO
    uint256 public totalSupply;// TODO -> delete it when delete erc20 !

    struct Booking{
        uint8 nSlotBooked;
        uint256 amountBooked;
        uint8 nSlotAskRefund;
        uint256 amountRefunded;
        State userState;
    }

    mapping(address => Booking) public bookings;
    address[] public bookingsAddress;

    constructor(
        string memory _origin, string memory _destination, 
        uint8 _nSlot, uint8 _price, uint _startTime, 
        address payable _conflictOwner, ModeConflict _carpoolingModeConflict
        ) public {

        owner = msg.sender;
        conflictOwner = _conflictOwner;

        origin = _origin;
        destination = _destination;
        nSlot = _nSlot;
        nAvailableSlot = nSlot;
        price = _price;
        startTime = _startTime;

        carpoolingModeConflict = _carpoolingModeConflict;

        bookings[owner].userState = State.booked;
        bookingsAddress.push(owner);
    }

}

contract BookingCarpooling is CreatingCarpooling {


    event AskRefund(address _booker, uint8 _nSlotAskRefund);
    event ValidetedRefund(address _booker, uint8 _nSlotAskRefundAllowed, uint256 _amountRefunded);
    event BookingDone(address _booker, uint8 _nSlotBooked, uint256 _amountBooked);

    modifier ownerOnly() {
        require(msg.sender == owner, "Only the owner can do it !");
        _;
    }

    // passenger booked slots (for the moment, owner has no choice, review it later !)
    function makeBooking(uint8 _nSlotBooked) payable public {
        require(_nSlotBooked > 0, "you have to book at least 1 slot");
        require(_nSlotBooked <= nAvailableSlot, "Not enough slot available, try to book less !");
        require(price*_nSlotBooked <= msg.value, "Please, pay the right price !"); // todo put == ?!
        nAvailableSlot -= _nSlotBooked;

        if (bookings[msg.sender].nSlotBooked + bookings[msg.sender].nSlotAskRefund > 0) {
            bookings[msg.sender].nSlotBooked = _nSlotBooked;
            bookings[msg.sender].amountBooked = msg.value;
        }else{
            bookings[msg.sender] = Booking(_nSlotBooked, msg.value, 0, 0, State.booked);
            bookingsAddress.push(msg.sender);
        }

        emit BookingDone(msg.sender, _nSlotBooked, msg.value);
    }

    // passenger ask to remove N slots and for refunds
    function askRefundBooking(uint8 _nSlotAskRefund) public {
        require(bookings[msg.sender].nSlotBooked > 0,"You don't have any slot booked !");
        require(bookings[msg.sender].nSlotBooked >= bookings[msg.sender].nSlotAskRefund + _nSlotAskRefund,"You don't have SO MANY slot booked !");

        bookings[msg.sender].nSlotAskRefund += _nSlotAskRefund;

        emit AskRefund(msg.sender, _nSlotAskRefund);
    }

    function allowRefundBooking(address _to, uint256 _amountRefunded) private {
        require(_amountRefunded > 0, "_amountRefunded must be > 0");
        require(_amountRefunded <= bookings[_to].amountBooked, "_amountRefunded must be <= amountBooked");
        bookings[_to].amountBooked -= _amountRefunded;
        bookings[_to].amountRefunded += _amountRefunded;
    }

    // Owner validate or not refunds: BUT passenger don't get back the assurance !
    function validateRefundBooking(address _to, bool isValidated) public ownerOnly {
        uint8 nSlotAskRefundAllowed;
        if (isValidated == true){
            nAvailableSlot += bookings[_to].nSlotAskRefund;
            allowRefundBooking(_to, bookings[_to].nSlotAskRefund * price);

            // refundOneUser(address passenger, bool isRefund, bool bookedRecipientIsConflictOwner, bool serviceRecipientToConflictOwner)

            nSlotAskRefundAllowed = bookings[_to].nSlotAskRefund;
            bookings[_to].nSlotBooked -= bookings[_to].nSlotAskRefund;
        }
        bookings[_to].nSlotAskRefund = 0;

        emit ValidetedRefund(_to, nSlotAskRefundAllowed, bookings[_to].amountRefunded);
    }

}


contract ManagingServices is BookingCarpooling {
    // simplification: 1 service has 1 address (if the assurance offer another service, then, it's preferable to use another address)
    // services = {userAddress: {serviceAddress: amountPaidByUser}}
    // KO for now: evolution: services = {userAddress: {serviceAddress: {amountPaidByUser: value, other info choosen by the service or guy setting it}}}
    mapping(address => mapping (address => uint256)) public services; 
    mapping(address => address[]) public servicesAddress;

    event CancelServiceToSomeone(address _service, address _passenger, address _to, uint256 _amountService);

    // _from service to the passenger/creator that is adding the service paying msg.value
    // todo : check if the setter has an amountBooked > 0... Not now, it reduce the freedom (ex: 3rd party doing it for X reason)
    function addService(address _from) payable public {
        require(msg.value >= 0, "price service must be >= 0");
        services[msg.sender][_from] += msg.value;
        servicesAddress[msg.sender].push(_from);
    }  

    // cancel service and give back client's token
    function cancelService(address _service, address _passenger, address _to) internal {
        uint256 amountService = services[_passenger][_service];
        services[_passenger][_service] = 0;
        bookings[_to].amountRefunded += amountService;

        emit CancelServiceToSomeone(_service, _passenger, _to, amountService);
    }

}

contract ManagingCarpooling is ManagingServices {
    // Passenger validate or not carpooling trip
    function validateCarpooling(bool _isValidated) public {
        require(bookings[msg.sender].userState == State.booked, "your carpooling's state must be 'booked'");
        require(bookings[msg.sender].nSlotBooked > 0, "you must have nSlotBooked > 0");

        if (_isValidated == false){
            bookings[msg.sender].userState = State.conflicted;
        }else{
            bookings[msg.sender].userState = State.validated;

            bookings[owner].amountRefunded += bookings[msg.sender].amountBooked;
            bookings[msg.sender].amountBooked = 0;

            for (uint8 i=0;i < servicesAddress[msg.sender].length;i++){
                address service = servicesAddress[msg.sender][i];
                uint256 amountService = services[msg.sender][service];

                services[msg.sender][service] = 0;
                bookings[service].amountRefunded += amountService;
            }
        }
    }



    function refundOneUser(address passenger, bool isRefund, bool noFilter, bool bookedRecipientIsConflictOwner, bool serviceRecipientToConflictOwner) private {
        address bookedRecipient = passenger;
        address serviceRecipient = passenger;

        if (bookedRecipientIsConflictOwner){
            bookedRecipient = conflictOwner;
        }
        if (serviceRecipientToConflictOwner){
            serviceRecipient = conflictOwner;
        }

        if (noFilter == false){
            isRefund = bookings[passenger].userState == State.conflicted;
        }

        if (isRefund){

            bookings[bookedRecipient].amountRefunded += bookings[passenger].amountBooked;
            bookings[passenger].amountBooked = 0;

            for(uint8 j; j < servicesAddress[passenger].length; j++){
                address service = servicesAddress[passenger][j];
                cancelService(service, passenger, serviceRecipient);
            }
        }
    }

    function refundSomeUsers(bool noFilter, bool bookedRecipientIsConflictOwner, bool serviceRecipientToConflictOwner) private {
        // refund passengers (booked + services) depending of the filter, and the serviceRecipientToConflictOwner option

        bool isRefund = noFilter;
        for(uint8 i; i < bookingsAddress.length; i++){
            address passenger = bookingsAddress[i];
            refundOneUser(passenger, isRefund, noFilter, bookedRecipientIsConflictOwner, serviceRecipientToConflictOwner);

        }
    }

    // call by a user to solve carpooling conflicts 
    // - caution in the case owner create fake account & fake passenger's booking that will validate the Tx...
    function solveConflict() public returns(bool){
        // Only passenger that didn't pay will get back their money 
        require(now > startTime + 24*60*60, "conflict can be solved after at least 24 hours");

        if(((carpoolingModeConflict == ModeConflict.generous) && (nConflict > 0)) ||
           ((nConflict == nSlot-nAvailableSlot) && (carpoolingModeConflict == ModeConflict.normal) && (nConflict > 1))
        ){
            // passenger get back money used for: booking, services
            refundSomeUsers(true, false, false);
            return true;
        }else{
            // transfer conflicted amountBooked and amountService is send to conflictOwner 
            refundSomeUsers(false, true, true);
            return false;
        }
    }

    // Owner can cancel the contract, every passenger can get his refund
    function cancelCarpooling() public ownerOnly {
        refundSomeUsers(true, false, false);
    }

}


contract Carpooling is ERC20Interface, CreatingCarpooling, BookingCarpooling, ManagingServices, ManagingCarpooling {




    constructor(string memory _origin, string memory _destination, 
        uint8 _nSlot, uint8 _price, uint _startTime, 
        address payable _conflictOwner, ModeConflict _carpoolingModeConflict
        ) public CreatingCarpooling(
        origin, destination, 
         nSlot, price, startTime, 
        conflictOwner, carpoolingModeConflict
        )  {}

    event Refunded(address _booker, uint256 _amountRefunded);



    // User (passenger or owner or ownerConflict or 3th party including assurance) can call withdrawalRefund _to an account not in the SC
    function withdrawalRefund(address payable _to) payable public {
        require(bookings[msg.sender].amountRefunded > 0, "you don't have anything to be refund !");

        _to.transfer(bookings[msg.sender].amountRefunded);
        bookings[msg.sender].amountRefunded = 0;

        emit Refunded(_to, bookings[msg.sender].amountRefunded);
    }


    // viewnSlotBooked by a passenger. !! todo : only the passenger & owner should be able to access this ?!
    function getnSlotBooked(address _bookingAddress) view public returns(uint) {
        return bookings[_bookingAddress].nSlotBooked;
    }

    // viewAmountBooked by a passenger. !! todo : only the passenger & owner should be able to access this ?!
    function getAmountBooked(address _bookingAddress) view public returns(uint) {
        return bookings[_bookingAddress].amountBooked;
    }

    function getUserState(address _bookingAddress) view public returns(State) {
        return bookings[_bookingAddress].userState;
    }

    function getnSlotAskRefund(address _bookingAddress) view public returns(uint8) {
        return bookings[_bookingAddress].nSlotAskRefund;
    }

    // viewServices by a passenger. !! todo : only the passenger & owner & services should be able to access this ?!
    function getAmountService(address _passenger, address _service) view public returns(uint256) {
        return services[_passenger][_service];
    }

    function getServicesAddress(address _passenger) view public returns(address[] memory) {
        return servicesAddress[_passenger];
    }



    // ERC20
    // get the balance of an address: refunded (=viewAmountRefunded)
    function balanceOf(address _from) external view returns (uint balance){
        balance = bookings[_from].amountRefunded;
    }

    // quite useless for us. review it. TODO
    // the person can use it to send token (amountRefunded) to another one in the contract
    function transfer(address _to, uint tokens) external returns (bool success){
        require(bookings[msg.sender].amountRefunded >= tokens, "sender has not enough tokens");

        bookings[msg.sender].amountRefunded -= tokens;
        bookings[_to].amountRefunded += tokens;
        emit Transfer(msg.sender, _to, tokens);

        return true;
    }

    // How many tokens can spender spend from the account (amountRefunded) of the person that call
    // X allows Y to access entirely or partially to his amountRefunded -> can be usefull for a 3rd party (i.e: blablacar)
    // but the 3rd party cannot AskRefund, only the passenger can.
    function allowance(address _from, address _to) view public returns(uint){
        return allowances[_from][_to];
    }

    function approve(address _to, uint256 tokens) public returns(bool success){
        require(bookings[msg.sender].amountRefunded >= tokens, "you don't have enough tokens");
        require(tokens > 0, "number of tokens must be positive");

        //todo check it
        allowances[msg.sender][_to] = tokens;
        emit Approval(msg.sender, _to, tokens);
        return true;
    }

    // B transfer _value from A's account to C' account
    // blablacar can transferFrom _from to _to some token for a reason, only if _from allowed it before !
    function transferFrom(address _from, address _to, uint256 _value) public returns (bool success) {

        require(_value > 0, "you have to transfer a positive number of token (_value)");
        require(allowances[_from][_to] >= _value, "you don't have any right on these token");
        require(bookings[_from].amountRefunded >= _value, "address owner has not enough token"); // double security useless ? TODO, usefull for the moment, so i don't need to reduce refund of each 3rd party

        allowances[_from][msg.sender] -= _value;
        bookings[_from].amountRefunded -= _value;
        bookings[_to].amountRefunded += _value;

        // Emit a transfer event
        emit Transfer(_from, _to, _value);
        return true;
    }    
}
